mapper.update:
UPDATE student
SET number       = #{number},
    name         = #{name},
    gender       = #{gender},
    dormitory_id = #{dormitoryId}
WHERE id = #{id};

mapper.select:
SELECT id
FROM student
WHERE dormitory_id = #{dormitoryId};

